namespace Geissweb\Euvat\Block\Adminhtml\Setup;

use Magento\Framework\View\Element\UiComponent\Control\ButtonProviderInterface;

class SetupButton implements ButtonProviderInterface
{
    /**
     * Save button
     * @return array
     */
    public function getButtonData()
    {
        return [
            'label' => __('Run Setup'),
            'class' => 'save primary',
            'data_attribute' => [
                'mage-init' => ['button' => ['event' => 'save']],
                'form-role' => 'save',
            ],
            'sort_order' => 30,
        ];
    }
}
