namespace Geissweb\Euvat\Model\System\Config\Source;

/**
 * Class DynamicShipping
 */
class DynamicShipping implements \Magento\Framework\Option\ArrayInterface
{
    const TYPE_DEFAULT = 0;
    const TYPE_BY_RATE_WITH_LARGEST_TOTAL = 1;
    const TYPE_BY_RATE_WITH_HIGHEST_RATE = 2;

    /**
     * @var array
     */
    protected $options = null;

    /**
     * @return array
     */
    public function toOptionArray()
    {
        if ($this->options === null) {
            $options = [
                [
                    'value' => self::TYPE_DEFAULT,
                    'label' => __('No')
                ],
                [
                    'value' => self::TYPE_BY_RATE_WITH_LARGEST_TOTAL,
                    'label' => __('Use the tax class with largest total')
                ],
                [
                    'value' => self::TYPE_BY_RATE_WITH_HIGHEST_RATE,
                    'label' => __('Use the tax class with the highest rate')
                ]
            ];

            $this->options = $options;
        }

        return $this->options;
    }
}
