namespace Geissweb\Euvat\Model\System\Config\Source;

/**
 * Class Interfaces
 */
class Interfaces implements \Magento\Framework\Option\ArrayInterface
{
    const VIES = 'Vies';
    const BZST = 'Bzst';

    /**
     * Provides select options
     * @return array
     */
    public function toOptionArray()
    {
        return [
            ['value' => self::VIES, 'label' => __('VIES')],
            //['value' => self::BZST, 'label' => __('BZSt (Germany only)')]
        ];
    }

    /**
     * Get options in "key-value" format
     *
     * @return array
     */
    public function toArray()
    {
        return [
            self::VIES => "VIES",
            //self::BZST => "BZSt",
        ];
    }
}
