namespace Geissweb\Euvat\Model\System\Config\Source;

/**
 * Class ValidationOptions
 */
class ValidationOptions implements \Magento\Framework\Data\OptionSourceInterface
{
    /**
     * Options getter
     *
     * @return array
     */
    public function toOptionArray()
    {
        return [
            [
                'value' => '',
                'label' => __('Optional')
            ],
            [
                'value' => 'valid-vat-if-specified',
                'label' => __('Optional, but require a valid VAT number when filled')
            ],
            [
                'value' => 'valid-vat-required',
                'label' => __('Require a valid VAT number (validated through the validation service)')
            ],
            [
                'value' => 'valid-vat-if-company-specified',
                'label' => __('Require a valid VAT number, when the company field is filled')
            ],
            [
                'value' => 'required-entry',
                'label' => __('Required (accept any value)')
            ]
        ];
    }
}
