define([
    'jquery',
    'Geissweb_Euvat/js/form/element/vat-number-base',
    'Magento_Ui/js/modal/alert',
    'Magento_Ui/js/lib/validation/validator',
    'mage/translate',
    'mageUtils',
    'uiRegistry',
], function (
    $,
    VatNumberBase,
    alert,
    validator,
    $t,
    Utils,
    registry,
) {
    'use strict';

    return VatNumberBase.extend({

        defaults: {
            isAdminhtml: true,
            addressId: '',
            template: 'ui/form/field',
            elementTmpl: 'ui/form/element/input',
            validation: {}
        },

        initialize: function (options) {
            this._super();
            this.setVisibility();
            return this;
        },

        /**
         * Sets initial visibility
         */
        setVisibility: function () {
            this.visible(true);
        },

        /**
         * Actions to happen after the validation
         * @param jqXHR
         */
        afterValidation: function (jqXHR) {
            var self = this;
            jqXHR.then(function () {
                if (typeof(self.response()) === 'object') {
                    var resp = self.response();
                    console.log("Response",resp);
                    if (resp.trader_name === null) {
                        resp.trader_name = "Not part of the validation response.";
                    }
                    if (resp.trader_address === null) {
                        resp.trader_address = "Not part of the validation response.";
                    }

                    alert({
                        title: $t('VAT number validation result'),
                        content: $t(resp.request_message)
                        +"<br/>"+"<br/>"
                        +$t('Trader Name: ')+resp.trader_name
                        +"<br/>"
                        +$t('Trader Address: ')+resp.trader_address
                        +"<br/>"+"<br/>"
                        +$t('Request Date: ')+resp.vat_request_date
                        +"<br/>"
                        +$t('Request Identifier: ')+resp.vat_request_id
                    });
                }
            });
        },

        /**
         * @returns string
         */
        getCountry: function () {
            var country = registry.get(this.parentName + '.' + 'country_id');
            if (typeof(country) !== 'object' || Utils.isEmpty(country.value())) {
                country = registry.get(this.parentName + '.' + 'country');
                if (typeof(country) !== 'object' || Utils.isEmpty(country.value())) {
                    if (this.debug) {
                        console.log("admin did not getCountry(): ", country);
                    }
                    return '';
                }
            }
            if (this.debug) {
                console.log("admin getCountry(): "+country.value());
            }
            return country.value();
        }

    });
});
