define([
    'ko',
    'jquery',
    'mage/storage',
    'mage/url',
    'Magento_Checkout/js/action/get-totals'
], function(ko, $, storage,urlBuilder, getTotalsAction) {

        'use strict';
        return function () {
            var serviceUrl = urlBuilder.build('euvat/checkout/recollect');
            return storage.post(
                serviceUrl,
                JSON.stringify({})
            ).done(
                function(response) {
                    if(response) {
                        var deferred = $.Deferred();
                        getTotalsAction([], deferred);
                    }
                }
            );/*.fail(
                function (response) {
                    //var error = JSON.parse(response.responseText);
                }
            );*/
        }
    }
);