define([
    'jquery',
    'Geissweb_Euvat/js/form/element/vat-number-base',
    'Geissweb_Euvat/js/queue',
    'Magento_Checkout/js/action/get-totals',
    'Magestore_OneStepCheckout/js/action/save-shipping-address',
    'Magestore_OneStepCheckout/js/action/set-shipping-information'
], function ($, VatNumberBase, queue, getTotals, magestoreSaveShippingAddress, magestoreSetShippingInfo) {
    'use strict';

    return VatNumberBase.extend({

        afterValidation: function () {
            var self = this;

            queue.addFunction(function () {
                var deferred = $.Deferred();
                if (self.countryCode.length > 0) {
                    $('body').trigger('processStop');
                    return self.updateCountry(self.countryCode, deferred);
                }
                return deferred.resolve();
            }).addFunction(function () {
                var deferred = $.Deferred();
                return magestoreSaveShippingAddress() || deferred.resolve();
            }).addFunction(function () {
                var deferred = $.Deferred();
                return magestoreSetShippingInfo() || deferred.resolve();
            }).addFunction(function () {
                var deferred = $.Deferred();
                return getTotals([]) || deferred.resolve();
            });

            queue.run();

        }

    });
});