/**
 * Thanks to http://stackoverflow.com/users/561731/neal
 */

define([
    'jquery'
], function ($) {
    "use strict";

    return {

        running: false,
        queue: [],

        run: function () {
            if (!this.running) {
                this.next();
            }
        },

        addFunction: function (callback) {
            var self = this;
            this.queue.push(function () {
                $.when(callback()).done(function () {
                    self.next();
                });
            });
            return this;
        },

        next: function () {
            this.running = false;
            var shift = this.queue.shift();
            if (shift) {
                this.running = true;
                shift();
            }
        }

    };

});