namespace Mageplaza\EmailAttachments\Model\Config\Backend;

use Magento\Config\Model\Config\Backend\File;

/**
 * Class TaCFile
 * @package Mageplaza\EmailAttachments\Model\Config\Backend
 */
class TaCFile extends File
{
    /**
     * The tail part of directory path for uploading
     */
    const UPLOAD_DIR = 'mageplaza/email_attachments/'; // Folder save image

    /**
     * Return path to directory for upload file
     *
     * @return string
     * @throw \Magento\Framework\Exception\LocalizedException
     */
    protected function _getUploadDir()
    {
        return $this->_mediaDirectory->getAbsolutePath($this->_appendScopeInfo(self::UPLOAD_DIR));
    }

    /**
     * Makes a decision about whether to add info about the scope.
     *
     * @return boolean
     */
    protected function _addWhetherScopeInfo()
    {
        return true;
    }

    /**
     * Getter for allowed extensions of uploaded files.
     *
     * @return string[]
     */
    protected function _getAllowedExtensions()
    {
        return ['pdf', 'doc', 'docx', 'txt'];
    }
}
